<?phpuse Src\Middleware\Connectors\DatabaseConnector;require 'bootstrap.php';$statement = "    CREATE TABLE IF NOT EXISTS history (        IdHistory INT NOT NULL AUTO_INCREMENT,        Filename VARCHAR(255) NULL,        Uploaded BIT(1) NOT NULL DEFAULT 0,        Location ENUM(\"remote\", \"local\") NOT NULL DEFAULT \"local\",        IdGoogleSpreadSheet VARCHAR(255) DEFAULT '',        AutoStamp DATETIME DEFAULT CURRENT_TIMESTAMP,        PRIMARY KEY (IdHistory)                     );";try {    (new DatabaseConnector())->getConnection()->exec($statement);    echo PHP_EOL. "---->Success adding `history` table<----" . PHP_EOL;} catch (\PDOException $e) {    exit($e->getMessage());}